package img

import (
	"bytes"
	"encoding/base64"
	"io"
	"strings"
	"testing"
)

const imgBase64 = ``

func TestCompress(t *testing.T) {
	// imgBase64 to file io.Reader
	index := strings.Index(imgBase64, ",")
	imgReader := base64.NewDecoder(base64.StdEncoding, strings.NewReader(imgBase64[index+1:]))
	// Reader not reset default, only support read once, convert to byte
	ImgByte, err := io.ReadAll(imgReader)
	if err != nil {
		t.Error(err)
		return
	}

	type args struct {
		file       io.Reader
		fileSize   int
		fileFormat string
	}
	tests := []struct {
		name             string
		args             args
		wantSmallSizeImg bool
		wantErr          bool
	}{
		{name: "compress img", args: args{file: bytes.NewReader(ImgByte), fileSize: len(ImgByte), fileFormat: ".png"}, wantSmallSizeImg: true, wantErr: false},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			gotNewFile, err := Compress(tt.args.file, tt.args.fileFormat)
			if (err != nil) != tt.wantErr {
				t.Errorf("Compress() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			gotFileByte, err := io.ReadAll(gotNewFile)
			if err != nil {
				t.Error(err)
				return
			}
			newFileSize := len(gotFileByte)
			if newFileSize > tt.args.fileSize {
				t.Errorf("Compress() gotNewFileSize = %v, bigger than originFileSize = %v. Compress fail", newFileSize, tt.args.fileSize)
			}
		})
	}
}
